import React, { useState } from 'react';
import { Box, VStack, HStack, Icon, Text, Spinner, Center, Alert, AlertIcon } from '@chakra-ui/react';
import { FiDatabase, FiUpload, FiFileText, FiBarChart2, FiLayers } from 'react-icons/fi'; // Removed unused imports
import { useDropzone } from 'react-dropzone';
import { uploadFileForPrediction } from '../services/api.js'; // Ensure this function is correctly defined in your services

const SidebarItem = ({ icon, label }) => (
  <HStack
    spacing={3}
    p={3}
    borderRadius="md"
    _hover={{ backgroundColor: 'gray.100', cursor: 'pointer' }}
    width="full"
  >
    <Icon as={icon} boxSize={5} />
    <Text>{label}</Text>
  </HStack>
);

const CropDisease = () => {
  const [prediction, setPrediction] = useState(null);
  const [confidence, setConfidence] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    accept: 'image/*',
    onDrop: (acceptedFiles) => {
      const file = acceptedFiles[0];
      setLoading(true);
      setError(null);

      uploadFileForPrediction(file)
        .then((response) => {
          const { prediction, confidence } = response.data;
          setPrediction(prediction);
          setConfidence(confidence);
        })
        .catch((error) => {
          setError('Error uploading the file or receiving the prediction.');
          console.error('Error details:', error.response ? error.response.data : error.message);
        })
        .finally(() => {
          setLoading(false);
        });
    }
  });

  return (
    <Box display="flex" minHeight="100vh">
      {/* Sidebar */}
      <Box
        as="nav"
        bg="white"
        w="350px"
        borderRight="1px solid #ddd"
        p={4}
        zIndex="1000"
        height="100vh"
      >
        <VStack align="flex-start" spacing={4}>
          <SidebarItem icon={FiDatabase} label="Crop Disease Classification" />
          <SidebarItem icon={FiUpload} label="Upload Data" />
          <SidebarItem icon={FiFileText} label="Annotate" />
          <SidebarItem icon={FiBarChart2} label="Dataset" />
          <SidebarItem icon={FiLayers} label="Analytics" />
        </VStack>
      </Box>

      {/* Main Content */}
      <Box flex="1" p={8}>
        <Center {...getRootProps()} border="2px dashed" borderColor="gray.300" borderRadius="md" p={4}>
          <input {...getInputProps()} />
          {isDragActive ? (
            <Text>Drop the file here...</Text>
          ) : (
            <Text>Drag 'n' drop some files here, or click to select files</Text>
          )}
        </Center>

        {loading && (
          <Center mt={4}>
            <Spinner />
          </Center>
        )}

        {error && (
          <Alert status="error" mt={4}>
            <AlertIcon />
            {error}
          </Alert>
        )}

        {prediction && (
          <Box mt={4}>
            <Text fontSize="lg" fontWeight="bold">Prediction: {prediction}</Text>
            <Text fontSize="md">Confidence: {confidence}</Text>
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default CropDisease;
