import React, { useState } from 'react';
import { Box, VStack, HStack, Icon, Text, Spinner, Center } from '@chakra-ui/react';
import { FiDatabase, FiUpload, FiFileText, FiBarChart2, FiLayers, FiPlayCircle, FiRefreshCw } from 'react-icons/fi';
import { useDropzone } from 'react-dropzone';

const SidebarItem = ({ icon, label }) => (
  <HStack
    spacing={3}
    p={3}
    borderRadius="md"
    _hover={{ backgroundColor: 'gray.100', cursor: 'pointer' }}
    width="full"
  >
    <Icon as={icon} boxSize={5} />
    <Text>{label}</Text>
  </HStack>
);

const CropDisease = () => {
  const [prediction, setPrediction] = useState(null);
  const [confidence, setConfidence] = useState(null);
  const [loading, setLoading] = useState(false);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    accept: 'image/*',
    onDrop: (acceptedFiles) => {
      setLoading(true);
      const file = acceptedFiles[0];
      const formData = new FormData();
      formData.append('file', file);

      fetch('http://localhost:5000/predict', {
        method: 'POST',
        body: formData,
      })
      .then(response => response.json())
      .then(data => {
        setLoading(false);
        setPrediction(data.prediction);
        setConfidence(data.confidence);
      })
      .catch(error => {
        setLoading(false);
        console.error('Error:', error);
      });
    }
  });

  return (
    <Box display="flex" minHeight="100vh">
      {/* Sidebar */}
      <Box
        as="nav"
        bg="white"
        w="350px"
        borderRight="1px solid #ddd"
        p={4}
        zIndex="1000"
        height="100vh"
      >
        <VStack align="flex-start" spacing={4}>
          <SidebarItem icon={FiDatabase} label="Crop Disease Classification" />
          <SidebarItem icon={FiUpload} label="Upload Data" />
          <SidebarItem icon={FiFileText} label="Annotate" />
          <SidebarItem icon={FiBarChart2} label="Dataset" />
          <SidebarItem icon={FiLayers} label="Analytics" />
        </VStack>
      </Box>

      {/* Main content */}
      <Box flex="1" p={6}>
        <Center
          {...getRootProps()}
          border="2px dashed"
          borderColor={isDragActive ? 'blue.500' : 'gray.300'}
          p={6}
          borderRadius="md"
          bg={isDragActive ? 'blue.50' : 'white'}
        >
          <input {...getInputProps()} />
          {loading ? (
            <Spinner size="xl" />
          ) : prediction ? (
            <VStack spacing={4} align="center">
              <Text fontSize="lg" fontWeight="bold">Prediction: {prediction}</Text>
              <Text fontSize="md">Confidence: {(confidence * 100).toFixed(2)}%</Text>
            </VStack>
          ) : (
            <Text fontSize="lg">Drag & drop an image here, or click to select one</Text>
          )}
        </Center>
      </Box>
    </Box>
  );
};

export default CropDisease;
