import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';

const LiveStock = () => {
  const [prediction, setPrediction] = useState(null); // State to store the prediction
  const [classification, setClassification] = useState(null); // State to store the classification

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    accept: 'image/*,video/*',
    onDrop: (acceptedFiles) => {
      console.log(acceptedFiles);
      // Here you would send the file to your model API for prediction
      // For now, we'll mock a prediction and classification
      const mockPrediction = "Foot-and-Mouth Disease"; // Replace this with actual prediction logic
      const mockClassification = "Classified as Severe"; // Replace with actual classification logic
      setPrediction(mockPrediction); // Set the prediction result
      setClassification(mockClassification); // Set the classification result
    }
  });

  return (
    <div style={{ textAlign: 'center', padding: '50px' }}>
      <div
        {...getRootProps()}
        style={{
          border: '4px dashed #ccc',
          borderRadius: '20px',
          padding: '40px',
          textAlign: 'center',
          backgroundColor: '#f1f1f1',
          width: '60%',
          height: '400px',
          margin: '0 auto',
          cursor: 'pointer',
          background: 'linear-gradient(to bottom, #8e44ad, #6a298c)',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <input {...getInputProps()} />
        <img
          src={require('/Users/rohansingh/Desktop/WebDev/LiveStock_Project/frontend/src/Images/dropdown_image_icon.jpeg')}
          alt="Upload Icon"
          style={{ width: '100px', marginBottom: '20px', borderRadius: '30px' }}
        />
        {isDragActive ? (
          <p style={{ fontSize: '18px', color: '#fff' }}>Drop the files here...</p>
        ) : (
          <p style={{ fontSize: '18px', color: '#fff' }}>Drag and drop or Click here to upload Image or Video.</p>
        )}
        <p style={{ fontSize: '14px', color: '#ddd' }}>
          Image and video size must be less than 5 MB.
        </p>
      </div>

      {/* Box to display the prediction */}
      {prediction && (
        <div
          style={{
            marginTop: '30px',
            padding: '20px',
            border: '2px solid #8e44ad',
            borderRadius: '10px',
            backgroundColor: '#fff',
            width: '50%',
            margin: '30px auto',
            boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
          }}
        >
          <h2 style={{ color: '#8e44ad', fontSize: '24px' }}>Predicted Disease:</h2>
          <p style={{ fontSize: '18px', color: '#333' }}>{prediction}</p>
        </div>
      )}

      {/* Box to display the classification */}
      {classification && (
        <div
          style={{
            marginTop: '20px',
            padding: '20px',
            border: '2px solid #6a298c',
            borderRadius: '10px',
            backgroundColor: '#fff',
            width: '50%',
            margin: '20px auto',
            boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
          }}
        >
          <h2 style={{ color: '#6a298c', fontSize: '24px' }}>Classification:</h2>
          <p style={{ fontSize: '18px', color: '#333' }}>{classification}</p>
        </div>
      )}
    </div>
  );
};

export default LiveStock;
