import React, { useState } from 'react';
import { Box, VStack, HStack, Icon, Text, Spinner, Center } from '@chakra-ui/react';
import { FiDatabase, FiUpload, FiFileText, FiBarChart2, FiLayers, FiPlayCircle, FiRefreshCw } from 'react-icons/fi';
import { useDropzone } from 'react-dropzone';

const SidebarItem = ({ icon, label }) => (
  <HStack
    spacing={3}
    p={3}
    borderRadius="md"
    _hover={{ backgroundColor: 'gray.100', cursor: 'pointer' }}
    width="full"
  >
    <Icon as={icon} boxSize={5} />
    <Text>{label}</Text>
  </HStack>
);

const CropDisease = () => {
  const [prediction, setPrediction] = useState(null);
  const [classification, setClassification] = useState(null);
  const [loading, setLoading] = useState(false);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    accept: 'image/*',
    onDrop: (acceptedFiles) => {
      setLoading(true);
      const file = acceptedFiles[0];
      const formData = new FormData();
      formData.append('file', file);

      fetch('http://localhost:5000/predict', {
        method: 'POST',
        body: formData,
      })
      .then(response => response.json())
      .then(data => {
        setLoading(false);
        setPrediction(data.prediction);
        setClassification(data.classification);
      })
      .catch(error => {
        setLoading(false);
        console.error('Error:', error);
      });
    }
  });

  return (
    <Box display="flex" minHeight="100vh">
      {/* Sidebar */}
      <Box
        as="nav"
        bg="white"
        w="350px"
        borderRight="1px solid #ddd"
        p={4}
        zIndex="1000"
        height="100vh"
      >
        <VStack align="flex-start" spacing={4}>
          <SidebarItem icon={FiDatabase} label="Crop Disease Classification" />
          <SidebarItem icon={FiUpload} label="Upload Data" />
          <SidebarItem icon={FiFileText} label="Annotate" />
          <SidebarItem icon={FiBarChart2} label="Dataset" />
          <SidebarItem icon={FiLayers} label="Analytics" />
          {/* Add more sidebar items as needed */}
        </VStack>
      </Box>

      {/* Main content */}
      <Box flex="1" p={6}>
        <Center>
          <Box
            {...getRootProps()}
            border="2px dashed"
            borderColor={isDragActive ? 'blue.300' : 'gray.300'}
            p={4}
            borderRadius="md"
            width="100%"
            maxWidth="600px"
            textAlign="center"
          >
            <input {...getInputProps()} />
            {loading ? (
              <Spinner size="lg" />
            ) : (
              <Text>
                {isDragActive ? 'Drop the files here...' : 'Drag & drop some files here, or click to select files'}
              </Text>
            )}
          </Box>
        </Center>

        {prediction && (
          <Box mt={6}>
            <Text fontSize="xl" fontWeight="bold">Prediction:</Text>
            <Text>{prediction}</Text>
            <Text fontSize="xl" fontWeight="bold">Classification:</Text>
            <Text>{classification}</Text>
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default CropDisease;
