import React from "react";
import NvBar from "./NvBar";
import {
  Card,
  CardBody,
  Stack,
  Text,
  Image,
} from "@chakra-ui/react";

const About = () => {
  return (
    <div
      style={{
        backgroundImage: `url(${require("/Users/rohansingh/Desktop/WebDev/LiveStock_Project/frontend/src/Images/What_do.jpg")})`, // Replace with your background image path
        backgroundSize: "cover",  // Ensures the image covers the entire div
        backgroundPosition: "center",  // Centers the image
        backgroundRepeat: "no-repeat", // Prevents the image from repeating
        minHeight: "100vh", // Makes sure the background covers the entire height of the page
      }}
    >
      <NvBar />
      <Card
        direction={{ base: "column", sm: "row" }}
        overflow="hidden"
        variant="outline"
        marginTop={"45px"}
        borderRadius={"30px"}
        marginLeft={"10px"}
        marginRight={"10px"}
        shadow={"md"}
      >
        <Image
          objectFit="cover"
          maxW={{ base: "100%", sm: "100px" }}
          src={require("../Images/Misson_img.jpg")} // Ensure the path is correct
          alt="Caffe Latte"
        />
        <Stack>
          <CardBody>
            <Text py="2">
              At the Farmers Disease Diagnostic/Reporting Portal, our mission is
              to empower farmers with the tools and knowledge they need to
              safeguard the health of their crops and livestock. By leveraging
              the power of Artificial Intelligence (AI), we aim to revolutionize
              agricultural health management, making it more accessible,
              efficient, and effective for farmers everywhere.
            </Text>
          </CardBody>
        </Stack>
      </Card>
      <Card
        direction={{ base: "column", sm: "row" }}
        overflow="hidden"
        variant="outline"
        marginTop={"45px"}
        borderRadius={"30px"}
        marginLeft={"10px"}
        marginRight={"10px"}
        shadow={"md"}
      >
        <Image
          objectFit="cover"
          maxW={{ base: "100%", sm: "100px" }}
          src={require("../Images/who_we_are_image.jpg")} // Ensure the path is correct
          alt="Caffe Latte"
        />

        <Stack>
          <CardBody>
            <Text py="2">
              We are a dedicated team of agricultural experts, technologists,
              and AI specialists committed to enhancing the productivity and
              sustainability of farms around the world. Our collective
              experience spans various fields, including agronomy, veterinary
              science, AI development, and rural development. Together, we are
              passionate about creating innovative solutions that address the
              unique challenges faced by farmers, particularly in remote or
              resource-limited areas.
            </Text>
          </CardBody>
        </Stack>
      </Card>
      <Card
        direction={{ base: "column", sm: "row" }}
        overflow="hidden"
        variant="outline"
        marginTop={"45px"}
        borderRadius={"30px"}
        marginLeft={"10px"}
        marginRight={"10px"}
        shadow={"md"}
      >
        <Image
          objectFit="cover"
          maxW={{ base: "100%", sm: "100px" }}
          src={require("../Images/What_do.jpg")} // Ensure the path is correct
          alt="Caffe Latte"
        />

        <Stack>
          <CardBody>
            <Text py="2">
              Our platform provides farmers with an AI-powered mobile portal
              designed to diagnose and report diseases affecting crops and
              livestock. Through this portal, farmers can:
            </Text>
          </CardBody>
        </Stack>
      </Card>
      <Card
        direction={{ base: "column", sm: "row" }}
        overflow="hidden"
        variant="outline"
        marginTop={"45px"}
        borderRadius={"30px"}
        marginLeft={"10px"}
        marginRight={"10px"}
        shadow={"md"}
      >
        <Image
          objectFit="cover"
          maxW={{ base: "100%", sm: "100px" }}
          src={require("../Images/why_choose_us_image.jpg")} // Ensure the path is correct
          alt="Caffe Latte"
        />

        <Stack>
          <CardBody>
            <Text py="2">
              Cutting-Edge Technology: Our platform uses state-of-the-art AI
              algorithms to provide fast, accurate, and actionable insights,
              helping farmers make informed decisions. Farmer-Centric Approach:
              We understand the challenges faced by farmers, and our solutions
              are designed with their needs in mind. Our user-friendly interface
              and accessible tools ensure that all farmers, regardless of
              technical expertise, can benefit from our services.
            </Text>
          </CardBody>
        </Stack>
      </Card>
    </div>
  );
};

export default About;
