import React, { useState, useEffect, useRef } from "react";
import { NavLink } from "react-router-dom";
import {
  Box,
  Flex,
  Link,
  Button,
  IconButton,
  Stack,
  useDisclosure,
  Image,
  HStack,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
} from "@chakra-ui/react";
import {
  HamburgerIcon,
  CloseIcon,
  ChevronLeftIcon,
  ChevronRightIcon,
} from "@chakra-ui/icons";
import { ChevronDownIcon } from "@chakra-ui/icons";
import TypingEffect from "./TypingEffect";
import Heading from "./Heading";
import Footer from "./Footer";
import HomePage from "./HomePage";
import About from "./About";
import ContactUs from "./ContactUs";

// ImageSlider Component
const ImageSlider = () => {
  const images = [
    require("/Users/rohansingh/Desktop/WebDev/LiveStock_Project/frontend/src/Images/Buff.jpeg"),
    require("/Users/rohansingh/Desktop/WebDev/LiveStock_Project/frontend/src/Images/cow.jpeg"),
    require("/Users/rohansingh/Desktop/WebDev/LiveStock_Project/frontend/src/Images/dese.jpeg"),
  ];

  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) =>
        prevIndex === images.length - 1 ? 0 : prevIndex + 1
      );
    }, 5000);

    return () => clearInterval(interval);
  }, [images.length]);

  const nextSlide = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevSlide = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  return (
    <Box
      position="relative"
      width="70%"
      maxW="700px"
      margin="0 auto"
      py={4}
      marginTop={"40px"}
      backgroundColor={"blue"}
    >
      <Image
        src={images[currentImageIndex]}
        alt={`Slide ${currentImageIndex}`}
        objectFit="cover"
        width="100%"
        height="400px"
        borderRadius="md"
        boxShadow="lg"
      />
      <IconButton
        aria-label="Previous Slide"
        icon={<ChevronLeftIcon />}
        position="absolute"
        left="20px"
        top="50%"
        transform="translateY(-50%)"
        onClick={prevSlide}
        colorScheme="teal"
      />
      <IconButton
        aria-label="Next Slide"
        icon={<ChevronRightIcon />}
        position="absolute"
        right="20px"
        top="50%"
        transform="translateY(-50%)"
        onClick={nextSlide}
        colorScheme="teal"
      />
    </Box>
  );
};

const Navbarr = () => {
  const { isOpen, onToggle } = useDisclosure();

  // Create refs for the Home, About, and ContactUs sections
  const homeSectionRef = useRef(null);
  const aboutSectionRef = useRef(null);
  const contactSectionRef = useRef(null);

  const scrollToHome = () => {
    homeSectionRef.current.scrollIntoView({ behavior: "smooth" });
  };

  const scrollToAbout = () => {
    aboutSectionRef.current.scrollIntoView({ behavior: "smooth" });
  };

  const scrollToContact = () => {
    contactSectionRef.current.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div>
      <Box
        bg="gray.800"
        color="white"
        px={4}
        py={3}
        shadow="md"
        position="sticky"
        top="0"
        zIndex="1000"
      >
        <Flex h={16} alignItems={"center"} justifyContent={"space-between"}>
          <HStack spacing={8} alignItems={"center"}>
            <Image
              src={require("/Users/rohansingh/Desktop/WebDev/LiveStock_Project/frontend/src/Images/farmer.jpg")}
              alt="Logo"
              boxSize="120px"
              objectFit="contain"
              borderRadius="full"
              marginTop={"50px"}
            />
            <HStack
              as={"nav"}
              spacing={8}
              display={{ base: "none", md: "flex" }}
              fontWeight="bold"
            >
              <Link
                onClick={scrollToHome}
                style={{ textDecoration: "none", color: "inherit" }}
              >
                Home
              </Link>
              <Link
                onClick={scrollToAbout}
                style={{ textDecoration: "none", color: "inherit" }}
              >
                About
              </Link>
              <Menu>
                <MenuButton
                  as={Link}
                  _hover={{ textDecoration: "none", color: "teal.300" }}
                  display="flex"
                  alignItems="center"
                >
                  LiveStock Diseases
                  <ChevronDownIcon ml={2} />
                </MenuButton>
                <MenuList bg="orange" color="black">
                  <MenuItem _hover={{ bg: "teal.300", color: "black" }}>
                    Disease 1
                  </MenuItem>
                  <MenuItem _hover={{ bg: "teal.300", color: "black" }}>
                    Disease 2
                  </MenuItem>
                  <MenuItem _hover={{ bg: "teal.300", color: "black" }}>
                    Disease 3
                  </MenuItem>
                </MenuList>
              </Menu>
              <Menu>
                <MenuButton
                  as={Link}
                  _hover={{ textDecoration: "none", color: "teal.300" }}
                  display="flex"
                  alignItems="center"
                >
                  Crop Diseases
                  <ChevronDownIcon ml={2} />
                </MenuButton>
                <MenuList bg="orange" color="black">
                  <MenuItem _hover={{ bg: "teal.300", color: "black" }}>
                    Disease A
                  </MenuItem>
                  <MenuItem _hover={{ bg: "teal.300", color: "black" }}>
                    Disease B
                  </MenuItem>
                  <MenuItem _hover={{ bg: "teal.300", color: "black" }}>
                    Disease C
