import React from 'react';
import { useDropzone } from 'react-dropzone';

const LiveStock = () => {
  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    accept: 'image/*,video/*',
    onDrop: (acceptedFiles) => {
      // Handle the uploaded files
      console.log(acceptedFiles);
    }
  });

  return (
    <div
      {...getRootProps()}
      style={{
        border: '2px dashed #ccc',
        borderRadius: '10px',
        padding: '20px',
        textAlign: 'center',
        backgroundColor: '#f9f9f9',
        width: '300px',
        margin: '50px auto',
        cursor: 'pointer',
        background: 'linear-gradient(to bottom, #9b59b6, #8e44ad)',
      }}
    >
      <input {...getInputProps()} />
      <img
        src={require('/Users/rohansingh/Desktop/WebDev/LiveStock_Project/frontend/src/Images/dropdown_image_icon.jpeg')}
        alt="Upload Icon"
        style={{ width: '50px', marginBottom: '20px' }}
      />
      {isDragActive ? (
        <p>Drop the files here...</p>
      ) : (
        <p>Drag and drop or Click here to upload Image or Video.</p>
      )}
      <p style={{ fontSize: '12px', color: '#666' }}>
        Image and video size must be less than 5 MB.
      </p>
    </div>
  );
};

export default LiveStock;
