import React, { useState, useEffect, useRef } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import {
  Box,
  Flex,
  Link,
  Button,
  IconButton,
  Stack,
  useDisclosure,
  Image,
  HStack,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
} from "@chakra-ui/react";
import {
  HamburgerIcon,
  CloseIcon,
  ChevronLeftIcon,
  ChevronRightIcon,
} from "@chakra-ui/icons";
import { ChevronDownIcon } from "@chakra-ui/icons";
import TypingEffect from "./TypingEffect";
import Heading from "./Heading";
import Footer from "./Footer";
import HomePage from "./HomePage";
import About from "./About";
import ContactUs from "./ContactUs";
import ImageSlider from "./ImageSlider"; // Assuming ImageSlider is in the same folder

// Import images at the top
import farmerImage from "./Images/farmer.jpg";

const Navbarr = () => {
  const { isOpen, onToggle } = useDisclosure();
  const navigate = useNavigate();

  // Create refs for the Home, About, and ContactUs sections
  const homeSectionRef = useRef(null);
  const aboutSectionRef = useRef(null);
  const contactSectionRef = useRef(null);

  const scrollToHome = () => {
    if (homeSectionRef.current) {
      homeSectionRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  const scrollToAbout = () => {
    if (aboutSectionRef.current) {
      aboutSectionRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  const scrollToContact = () => {
    if (contactSectionRef.current) {
      contactSectionRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  const goToLivestock = () => {
    navigate("/livStock");
  };

  const goToCropstock = () => {
    navigate("/crop-disease");
  };

  return (
    <div>
      <Box
        bg="gray.800"
        color="white"
        px={4}
        py={3}
        shadow="md"
        position="sticky"
        top="0"
        zIndex="1000"
      >
        <Flex h={16} alignItems={"center"} justifyContent={"space-between"}>
          <HStack spacing={8} alignItems={"center"}>
            <Image
              src={farmerImage}
              alt="Logo"
              boxSize="120px"
              objectFit="contain"
              borderRadius="full"
              marginTop={"50px"}
            />
            <HStack
              as={"nav"}
              spacing={8}
              display={{ base: "none", md: "flex" }}
              fontWeight="bold"
            >
              <Link
                onClick={scrollToHome}
                style={{ textDecoration: "none", color: "inherit" }}
              >
                Home
              </Link>
              <Link
                onClick={scrollToAbout}
                style={{ textDecoration: "none", color: "inherit" }}
              >
                About
              </Link>
              <Link
                onClick={goToLivestock}
                style={{ textDecoration: "none", color: "inherit" }}
              >
                Livestock Diseases
              </Link>
              <Link
                onClick={goToCropstock}
                style={{ textDecoration: "none", color: "inherit" }}
              >
                Crop Diseases
              </Link>
              <Link
                onClick={scrollToContact}
                style={{ textDecoration: "none", color: "inherit" }}
              >
                Contact
              </Link>
            </HStack>
          </HStack>
          <Flex alignItems={"center"}>
            <Button
              variant={"solid"}
              colorScheme={"teal"}
              size={"sm"}
              mr={4}
              _hover={{ bg: "teal.400" }}
            >
              Sign In
            </Button>
            <IconButton
              size={"md"}
              icon={isOpen ? <CloseIcon /> : <HamburgerIcon />}
              aria-label={"Open Menu"}
              display={{ md: "none" }}
              onClick={onToggle}
            />
          </Flex>
        </Flex>

        {isOpen && (
          <Box pb={4} display={{ md: "none" }}>
            <Stack as={"nav"} spacing={4}>
              <Link
                onClick={scrollToHome}
                _hover={{ textDecoration: "none", color: "teal.300" }}
              >
                Home
              </Link>
              <Link
                onClick={scrollToAbout}
                _hover={{ textDecoration: "none", color: "teal.300" }}
              >
                About
              </Link>
              <Link
                onClick={goToLivestock}
                _hover={{ textDecoration: "none", color: "teal.300" }}
              >
                Livestock Diseases
              </Link>
              <Link
                onClick={goToCropstock}
                _hover={{ textDecoration: "none", color: "teal.300" }}
              >
                Crop Diseases
              </Link>
              <Link
                onClick={scrollToContact}
                _hover={{ textDecoration: "none", color: "teal.300" }}
              >
                Contact
              </Link>
            </Stack>
          </Box>
        )}
      </Box>
      <div
        style={{ backgroundColor: "#dce294", padding: "50px" }}
        ref={homeSectionRef}
      >
        <TypingEffect />
        <Heading />
        <ImageSlider />
      </div>
      <div ref={aboutSectionRef}>
        <About />
      </div>
      <div ref={contactSectionRef}>
        <ContactUs />
      </div>
      <Footer />
    </div>
  );
};

export default Navbarr;
