import React, { useState } from 'react';
import { Box, VStack, HStack, Icon, Text } from '@chakra-ui/react';
import { FiDatabase, FiUpload, FiFileText, FiBarChart2, FiLayers, FiPlayCircle, FiRefreshCw } from 'react-icons/fi';
import { useDropzone } from 'react-dropzone';

const SidebarItem = ({ icon, label }) => (
  <HStack
    spacing={3}
    p={3}
    borderRadius="md"
    _hover={{ backgroundColor: 'gray.100', cursor: 'pointer' }}
    width="full"
  >
    <Icon as={icon} boxSize={5} />
    <Text>{label}</Text>
  </HStack>
);

const LiveStock = () => {
  const [prediction, setPrediction] = useState(null);
  const [classification, setClassification] = useState(null);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    accept: 'image/*,video/*',
    onDrop: (acceptedFiles) => {
      console.log(acceptedFiles);
      const mockPrediction = "Foot-and-Mouth Disease";
      const mockClassification = "Classified as Severe";
      setPrediction(mockPrediction);
      setClassification(mockClassification);
    }
  });

  return (
    <Box display="flex" minHeight="100vh">
      {/* Sidebar */}
      <Box
        as="nav"
        bg="white"
        w="350px"
        borderRight="1px solid #ddd"
        p={4}
        zIndex="1000"
        height="100vh"
      >
        <VStack align="flex-start" spacing={4}>
          <SidebarItem icon={FiDatabase} label="Livestock_disease_classification" />
          <SidebarItem icon={FiUpload} label="Upload Data" />
          <SidebarItem icon={FiFileText} label="Annotate" />
          <SidebarItem icon={FiBarChart2} label="Dataset" />
          <SidebarItem icon={FiLayers} label="Analytics" />
          <SidebarItem icon={FiPlayCircle} label="Generate" />
          <SidebarItem icon={FiRefreshCw} label="Deployments" />
          <SidebarItem icon={FiRefreshCw} label="Active Learning" />
        </VStack>
      </Box>

      {/* Main Content */}
      <Box
        flex="1"
        display="flex"
        justifyContent="center"
        // alignItems="center"
        textAlign="center"
        p={6}
        background="#f1f1f1"
      >
        <Box
          {...getRootProps()}
          style={{
            border: '4px dashed #ccc',
            borderRadius: '20px',
            padding: '40px',
            textAlign: 'center',
            backgroundColor: '#f1f1f1',
            width: '80%',
            height: '300px',
            cursor: 'pointer',
            background: 'linear-gradient(to bottom, #2980b9, #2c3e50)',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <input {...getInputProps()} />
          <img
            src={require('/Users/rohansingh/Desktop/WebDev/LiveStock_Project/frontend/src/Images/dropdown_image_icon.jpeg')}
            alt="Upload Icon"
            style={{ width: '100px', marginBottom: '20px', borderRadius: '30px' }}
          />
          {isDragActive ? (
            <p style={{ fontSize: '18px', color: '#fff' }}>Drop the files here...</p>
          ) : (
            <p style={{ fontSize: '18px', color: '#fff' }}>Drag and drop or Click here to upload Image or Video.</p>
          )}
          <p style={{ fontSize: '14px', color: '#ddd' }}>
            Image and video size must be less than 5 MB.
          </p>
        </Box>
      </Box>
    </Box>
  );
};

export default LiveStock;
